#!/usr/bin/env bash

# What do I do?
###############

# This is the last step of the process and it generates the predictions using the models
# generated by `build-models.sh`.
# Once the `build-models.sh` script was ran, this script can be ran multiple times with
# different inputs.
# The predictions are stored in $APP_OUTPUT_FILE_PREDICTIONS.


# CONFIGURATION SECTION
#######################

# Set the global configuration

rundir="`dirname "$0"`"
rundir="`cd "$rundir"; pwd`"
. "$rundir"/setenv.sh


# RUN SECTION
#############

spark-submit \
  --class "com.lunatic.mlx.cuisines.BuildPredictions" \
  --name "CuisineDataPrediction" \
  --master $SPARK_MASTER_URL \
  --num-executors $YARN_EXECUTORS \
  --conf spark.driver.host=$SPARK_DRIVER_HOST \
  --conf spark.task.maxFailures=20 \
  --conf spark.driver.memory=$SPARK_DRIVER_MEMORY \
  --conf spark.executor.memory=$SPARK_EXECUTOR_MEMORY \
  --verbose \
  $JAR_FILE \
  app.input.file.training="$APP_INPUT_FILE_TRAINING" \
  app.input.file.test="$APP_INPUT_FILE_TEST" \
  app.output.file.predictions="$APP_OUTPUT_FILE_PREDICTIONS" \
  app.model.dir="$APP_MODEL_DIR"
